import { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import getText from './redux/actions';


function App() {
  const [input, setInput] = useState('');
  const texts = useSelector(state => state.texts);
  const dispatch = useDispatch();

  const handleChange = ( e ) => {
    setInput(e.target.value)
  }

  const handleSubmit = ( e ) => {
    e.preventDefault();
    if(input) {
      dispatch(getText(input))
      setInput('');
    };
  }

  return (
    <div className="App">
      <nav className='navbar navbar-dark bg-red'>
        <form onSubmit={handleSubmit}>
          <input type='text' value={input} placeholder='Insert Text' onChange={handleChange}></input>
          <button type='submit' className='btn btn-primary btn-md active'>Send</button>
        </form>
      </nav>
    </div>
  );
}

export default App;
