function App() {
  const [input, setInput] = useState('');
  const [texts, setTexts] = useState([]);

  const save_localstorage = () => {

  };

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = await fetch(`http://localhost:3001/reverse?text=${input}`, {
      mode: 'no-cors'
    });
    setTexts([...texts, await data.json()]);
    setInput('');
  }

  useEffect(() => {
    console.log('textos:  ', texts);
  },[texts]);

  return (
    <div className="App">
      <h1>Helloo World</h1>
      <form onSubmit={handleSubmit}>
        <input value={input} placeholder='Insert Text' onChange={handleChange}></input>
        <button type='submit' className='btn btn-primary btn-md active'>Send</button>
      </form>
    </div>
  );
}

export default App;
