import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [input, setInput] = useState('');
  const [texts, setTexts] = useState([]);

  const save_localstorage = () => {

  };

  const fetchdata = async () => {
    const data = await fetch(`http://localhost:3001/reverse?text=${input}`, {
      method:'GET',
        headers:{
            'Accept':'application/json',
            'Content-Type':'application/json',
        }
    });
    const response = await data.json()
    console.log(response)
  }

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    fetchdata();
    setInput('');
  }

  return (
    <div className="App">
      <h1>Helloo World</h1>
      <form onSubmit={handleSubmit}>
        <input value={input} placeholder='Insert Text' onChange={handleChange}></input>
        <button type='submit' className='btn btn-primary btn-md active'>Send</button>
      </form>
    </div>
  );
}

export default App;
