import { useSelector } from "react-redux";
import '../results.css';

const Results = () => {
    const { texts } = useSelector(state => state);

    return(
        <>
            <div className="container-results">
                <h3>Results:</h3>
                {texts?.map( t => <div key={t.inverted}>{t.inverted}</div> )}
            </div>
        </>
    )
};

export default Results;
