import GET_TEXT from './constants';

const getText = (input) => {
    return async (dispatch) => {
        try {
            const text = await fetch(`http://localhost:3001/reverse?text=${input}`, {
                mode: 'no-cors'
            });
            const response = await text.json();
            dispatch({type: GET_TEXT, payload: response});
        } catch (error) {
            alert({error: error.message});
        };
    };
};

export default getText;
