import GET_TEXT from './constants';

const getText = (input) => {
    return async (dispatch) => {
        try {
            const text = await (await fetch(`http://localhost:3001/reverse?text=${input}`, {
                mode: 'no-cors'
            })).json();
            dispatch({type: GET_TEXT, payload: text});
        } catch (error) {
            alert(error.message);
        };
    };
};

export default getText;
